# Fix all Git merge conflict markers - keeps content after =======
Get-ChildItem -Path . -Include *.js,*.jsx,*.ts,*.tsx,*.json,*.py,*.java,*.cpp,*.c,*.h,*.css,*.html -Recurse -File -ErrorAction SilentlyContinue | ForEach-Object { $f=$_.FullName; $c=[System.IO.File]::ReadAllText($f,[System.Text.Encoding]::UTF8); if($c -match '<<<<<<<'){ while($c -match '<<<<<<<'){$c=$c -replace '(?s)<<<<<<< HEAD.*?=======\s*\r?\n(.*?)\r?\n>>>>>>> [^\r\n]+','$1'}; [System.IO.File]::WriteAllText($f,$c,[System.Text.UTF8Encoding]::new($false)); Write-Host "Fixed: $($_.Name)" } }; Write-Host "Done!" -ForegroundColor Green
